package com.capgemini.javademos;

public class LoopingStatements1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int counter = 1; counter <=1000 ; counter ++)   //(int counter = 1000; counter<=1;counter--)
		{	
			System.out.print("Value of counter:");
			System.out.println(counter);
		}


	}

}
